Script.core:
import VR

if dev.intersect():
	print dev.getIntersection()

#version 400 compatibility

uniform sampler3D tex;

in float cylR1;
in float cylR2;
in vec3 cylDir;
in vec3 cylP0;
in vec3 cylP1;
in vec3 cylN0;
in vec3 cylN1;
in vec3 dir;

in vec3 ViewDirection;
in vec3 fvObjectPosition;
in vec3 MVPos;
in vec3 Normal;

vec3 norm;
vec3 position;
vec4 color;
vec3 tc;
mat4 miMV;
vec3 rayStart;
vec3 rayDir;

#define eps 0.0001
#define mP gl_ProjectionMatrix

vec2 solveEq(float A, float B, float C) {
   	float D = B*B-4.0*A*C;
   	if (D < 0.0) discard; // no solution/intersection
   	D = sqrt(D);
   	float t1 = (-B+D)/A*0.5;
   	float t2 = (-B-D)/A*0.5;
   	return vec2(t1, t2);
}

float intersectCap(vec3 rayStart, vec3 rayDir, vec3 p0, vec3 n, float r) {
	float t = dot((p0-rayStart),n)/dot(rayDir,n);
   	vec3 p = rayStart + t*rayDir;
   	if ( abs(dot(n, p-p0)) > eps ) return -1.0;
   	if ( distance(p,p0) > r ) return -1.0;
   	return t;
}

// ellipsoid: (x/a)2 + (y/b)2 + (z/c)2 = 1
vec3 applyCaps(vec3 rayStart, vec3 rayDir, float tp) {
   	vec3 pC = rayStart + tp*rayDir;
   	if ( dot(cylN0, pC-cylP0) < -eps ) {
   		float tc0 = intersectCap(rayStart, rayDir, cylP0, cylN0, cylR1);
   		if (tc0 > eps) {
   			tp = max( tp, tc0 );
   			pC = rayStart + tp*rayDir;
   		} else discard;
   	}

   	if ( dot(cylN1, pC-cylP1) >  eps )  {
   		float tc1 = intersectCap(rayStart, rayDir, cylP1, cylN1, cylR2);
   		if (tc1 > eps) {
   			tp = max( tp, tc1 );
   			pC = rayStart + tp*rayDir;
   		} else discard;
   	}
   	return pC;
}

vec3 raycastCylinder() {
   	vec3 rayDRad = rayDir - dot(rayDir, cylDir)*cylDir;
   	vec3 rayPRad = rayStart-cylP0 - dot(rayStart-cylP0, cylDir)*cylDir;

   	float A = dot(rayDRad,rayDRad);
   	float B = 2.0*dot(rayDRad, rayPRad);
   	float C = dot(rayPRad, rayPRad) - cylR1*cylR1;
   	vec2 t = solveEq(A, B, C);
   	float tp = min(t[0],t[1]);
   	return applyCaps(rayStart, rayDir, tp);
}

vec3 raycastCone() {
   	float H = distance(cylP0, cylP1);
   	float H2 = H*H;
   	float dR = cylR1-cylR2;
   	float dR2 = dR*dR;

   	if (abs(dR) < eps) return raycastCylinder();
   	//return fvObjectPosition;

   	vec3 cylPa = cylP0 + cylDir * cylR1*H/dR;
   	float vrvc = dot(rayDir, cylDir);
   	float dpvc = dot(rayStart-cylPa, cylDir);
   	vec3 rayDRad = rayDir - vrvc*cylDir;
   	vec3 rayPRad = rayStart-cylPa - dpvc*cylDir;
   	float cos2a = H2 / (H2 + dR2);
   	float sin2a = 1.0 - cos2a;

   	float A = cos2a * dot(rayDRad,rayDRad) - sin2a * vrvc * vrvc;
   	float B = 2.0 * (cos2a * dot(rayDRad, rayPRad) - sin2a * vrvc * dpvc);
   	float C = cos2a * dot(rayPRad, rayPRad) - sin2a * dpvc * dpvc;
   	vec2 t = solveEq(A, B, C);
   	float tp = min(t[0],t[1]);
   	return applyCaps(rayStart, rayDir, tp);
}

const vec2 size = vec2(0.1,-0.1);
const ivec3 off = ivec3(-1,0,1);
void applyBumpMap() {
    float s11 = texture(tex, tc).r;
    float s01 = textureOffset(tex, tc, off.xyz).r;
    float s21 = textureOffset(tex, tc, off.zyx).r;
    float s10 = textureOffset(tex, tc, off.yxz).r;
    float s12 = textureOffset(tex, tc, off.yzx).r;

    vec3 va = normalize(vec3(size.xy,s21-s01));
    vec3 vb = normalize(vec3(size.yx,s12-s10));
    norm = normalize(norm+0.8*cross(va,vb));
    //norm = cross(va,vb);
}

void computeNormal() {
   	norm = position - cylP0 - dot(position - cylP0, cylDir)*cylDir;
	norm = (miMV*vec4(norm, 0.0)).xyz;
   	norm = normalize(norm);
}

void computeTexCoords() {
	float h = dot(position-cylP0, cylDir);
	tc = vec3(norm.x+h*0.5, h*0.3/cylR1, norm.z+h*0.5);
}

void computeDepth() {
	vec4 pp = mP * vec4(position, 1);
	float d = pp.z / pp.w;
	gl_FragDepth = d*0.5 + 0.5;
}

void main( void ) {
	miMV = inverse( gl_ModelViewMatrix ); // TODO: avoid computing inverse in shader!
	norm = Normal;
   	rayStart = vec3(0.0);
   	rayDir = MVPos;
   	rayDir = normalize(rayDir);

	position = fvObjectPosition;
	position = raycastCone();
	computeNormal();
	computeTexCoords();
	applyBumpMap();
	color = texture(tex, tc);
	computeDepth();

	norm = gl_NormalMatrix * norm;
    gl_FragData[0] = vec4(position, 1.0);
    gl_FragData[1] = vec4(norm, 1.0);
    gl_FragData[2] = vec4(color.xyz, 0);
}

#version 400 compatibility

uniform sampler3D tex;

in vec3 vn;
in vec4 vc;
in vec3 vp;

vec4 color;

void applyBlinnPhong() {
	vec3 n = gl_NormalMatrix * vn;
	vec3  light = normalize( gl_LightSource[0].position.xyz );// directional light
	float NdotL = max(dot( n, light ), 0.0);
	vec4  ambient = gl_LightSource[0].ambient * color;
	vec4  diffuse = gl_LightSource[0].diffuse * NdotL * color;
	float NdotHV = max(dot(n, normalize(gl_LightSource[0].halfVector.xyz)),0.0);
	vec4  specular = gl_LightSource[0].specular * pow( NdotHV, gl_FrontMaterial.shininess ); 
	gl_FragColor = ambient + diffuse + specular;
}

void main( void ) {
	color = vc;
	vec4 noise = texture(tex,vp.xyz);
	color.xyz *= noise.a;
	//if (color.a < 0.5) discard;
	//gl_FragColor = c;
	applyBlinnPhong();
}

// vertex shader program
varying vec3 vn;
varying vec4 vc;
varying vec3 vp;

attribute vec4 osg_Vertex;
attribute vec3 osg_Normal;
attribute vec4 osg_Color;

void main( void ) {
	vn = osg_Normal;
	vc = osg_Color;
	vp = osg_Vertex.xyz;
	gl_Position = gl_ModelViewProjectionMatrix*osg_Vertex;
}

#version 400 compatibility

uniform sampler2D tex;

in vec2 tcs;
in vec3 norm;
vec4 color;


void applyBlinnPhong() {
	vec3 n = gl_NormalMatrix * norm;
	vec3  light = normalize( gl_LightSource[0].position.xyz );// directional light
	float NdotL = max(dot( n, light ), 0.0);
	vec4  ambient = gl_LightSource[0].ambient * color;
	vec4  diffuse = gl_LightSource[0].diffuse * NdotL * color;
	float NdotHV = max(dot(norm, normalize(gl_LightSource[0].halfVector.xyz)),0.0);
	vec4  specular = gl_LightSource[0].specular * pow( NdotHV, gl_FrontMaterial.shininess ); 
	gl_FragColor = ambient + diffuse + specular;
}

void main( void ) {
	color = texture(tex,tcs);
	if (color.a < 0.5) discard;
	color.a = 1.0;
	color.y *= 0.7;
	color.xz *= 0.2;
	//gl_FragColor = c;
	applyBlinnPhong();
	//gl_FragColor = vec4(norm, 1.0);
	//gl_FragColor = vec4(0,1,0,1);
}

//----------------------------------------------------------------------------------------------MAIN--GP
#version 400 compatibility
#extension GL_EXT_geometry_shader4 : enable
layout (points) in;
layout (triangle_strip, max_vertices = 6) out;
in vec3 vn[];
out vec2 tcs;
out vec3 norm;

#define mMVP gl_ModelViewProjectionMatrix
#define mMV gl_ModelViewMatrix
#define mP gl_ProjectionMatrix

vec4 crns[4];
vec4 crnsMVP[4];
vec2 TCS[4];

void emit(int i) {
	gl_Position = crnsMVP[i];
	tcs = TCS[i];
	EmitVertex();
}

void main() {
	vec4 p = gl_PositionIn[0];
	float s = 0.03;
	vec3 u = vec3(0,1,0);
	//vec4 x = vec4(s,0,0,0);
	//vec4 z = vec4(0,0,s,0);
	vec4 z = vec4(vn[0]*s, 0);
	vec4 x = vec4(normalize(cross(z.xyz,u))*s, 0);
	if (z[1] > 0) z[1] *= -1;
	crns[0] = p -x-z;
	crns[1] = p +x-z;
	crns[2] = p -x+z;
	crns[3] = p +x+z;
	TCS[0] = vec2(0,0);
	TCS[1] = vec2(1,0);
	TCS[2] = vec2(0,1);
	TCS[3] = vec2(1,1);
	norm = normalize(cross(x.xyz,z.xyz));
	for (int i=0; i<4; i++) crnsMVP[i] = mMVP * crns[i];
	emit(0);
	emit(1);
	emit(2);
	emit(3);
	EndPrimitive();
}

// vertex shader program
varying vec3 vn;

attribute vec4 osg_Vertex;
attribute vec3 osg_Normal;

void main( void ) {
	vn = osg_Normal;
    gl_Position = osg_Vertex;
}

#version 400 compatibility
#extension GL_ARB_tessellation_shader : enable

layout(vertices = 2) out;
out vec3 tcPosition[];
in vec2 vtc[];
out vec2 ctc[];
in vec3 vn[];
out vec3 cn[];

#define ID gl_InvocationID

void main() {
    tcPosition[ID] = gl_in[ID].gl_Position.xyz;
    ctc[ID] = vtc[ID];
    cn[ID] = vn[ID];

    if (ID == 0) {
        gl_TessLevelOuter[0] = 1;
        gl_TessLevelOuter[1] = 4;
    }
}

#version 400 compatibility
#extension GL_ARB_tessellation_shader : enable

layout( isolines ) in;
in vec3 tcPosition[];
in vec2 ctc[];
in vec3 cn[];
out vec2 tc;

void main() {
    float u = gl_TessCoord.x;

    vec3 n1 = cn[0];
    vec3 n2 = cn[1];
    vec3 p1 = tcPosition[0];
    vec3 p2 = p1+n1*0.1;
    vec3 p3 = tcPosition[1];

    vec3 pos = (1.0-u)*(1.0-u)*p1 + 2.0*(1.0-u)*u*p2 + u*u*p3;

    tc = mix(ctc[0], ctc[1], u);
    gl_Position = vec4(pos, 1.0);
}

#version 400 compatibility

uniform sampler3D tex;

in float cylR1;
in float cylR2;
in vec3 cylDir;
in vec3 cylP0;
in vec3 cylP1;
in vec3 cylN0;
in vec3 cylN1;
in vec3 dir;

in vec3 ViewDirection;
in vec3 fvObjectPosition;
in vec3 MVPos;
in vec3 Normal;

vec3 norm;
vec3 position;
vec4 color;
vec3 tc;
mat4 miMV;
vec3 rayStart;
vec3 rayDir;

#define eps 0.0001
#define mP gl_ProjectionMatrix

vec2 solveEq(float A, float B, float C) {
   	float D = B*B-4.0*A*C;
   	if (D < 0.0) discard; // no solution/intersection
   	D = sqrt(D);
   	float t1 = (-B+D)/A*0.5;
   	float t2 = (-B-D)/A*0.5;
   	return vec2(t1, t2);
}

float intersectCap(vec3 rayStart, vec3 rayDir, vec3 p0, vec3 n, float r) {
	float t = dot((p0-rayStart),n)/dot(rayDir,n);
   	vec3 p = rayStart + t*rayDir;
   	if ( abs(dot(n, p-p0)) > eps ) return -1.0;
   	if ( distance(p,p0) > r ) return -1.0;
   	return t;
}

// ellipsoid: (x/a)2 + (y/b)2 + (z/c)2 = 1
vec3 applyCaps(vec3 rayStart, vec3 rayDir, float tp) {
   	vec3 pC = rayStart + tp*rayDir;
   	if ( dot(cylN0, pC-cylP0) < -eps ) {
   		float tc0 = intersectCap(rayStart, rayDir, cylP0, cylN0, cylR1);
   		if (tc0 > eps) {
   			tp = max( tp, tc0 );
   			pC = rayStart + tp*rayDir;
   		} else discard;
   	}

   	if ( dot(cylN1, pC-cylP1) >  eps )  {
   		float tc1 = intersectCap(rayStart, rayDir, cylP1, cylN1, cylR2);
   		if (tc1 > eps) {
   			tp = max( tp, tc1 );
   			pC = rayStart + tp*rayDir;
   		} else discard;
   	}
   	return pC;
}

vec3 raycastCylinder() {
   	vec3 rayDRad = rayDir - dot(rayDir, cylDir)*cylDir;
   	vec3 rayPRad = rayStart-cylP0 - dot(rayStart-cylP0, cylDir)*cylDir;

   	float A = dot(rayDRad,rayDRad);
   	float B = 2.0*dot(rayDRad, rayPRad);
   	float C = dot(rayPRad, rayPRad) - cylR1*cylR1;
   	vec2 t = solveEq(A, B, C);
   	float tp = min(t[0],t[1]);
   	return applyCaps(rayStart, rayDir, tp);
}

vec3 raycastCone() {
   	float H = distance(cylP0, cylP1);
   	float H2 = H*H;
   	float dR = cylR1-cylR2;
   	float dR2 = dR*dR;

   	if (abs(dR) < eps) return raycastCylinder();
   	//return fvObjectPosition;

   	vec3 cylPa = cylP0 + cylDir * cylR1*H/dR;
   	float vrvc = dot(rayDir, cylDir);
   	float dpvc = dot(rayStart-cylPa, cylDir);
   	vec3 rayDRad = rayDir - vrvc*cylDir;
   	vec3 rayPRad = rayStart-cylPa - dpvc*cylDir;
   	float cos2a = H2 / (H2 + dR2);
   	float sin2a = 1.0 - cos2a;

   	float A = cos2a * dot(rayDRad,rayDRad) - sin2a * vrvc * vrvc;
   	float B = 2.0 * (cos2a * dot(rayDRad, rayPRad) - sin2a * vrvc * dpvc);
   	float C = cos2a * dot(rayPRad, rayPRad) - sin2a * dpvc * dpvc;
   	vec2 t = solveEq(A, B, C);
   	float tp = min(t[0],t[1]);
   	return applyCaps(rayStart, rayDir, tp);
}

const vec2 size = vec2(0.1,-0.1);
const ivec3 off = ivec3(-1,0,1);
void applyBumpMap() {
    float s11 = texture(tex, tc).r;
    float s01 = textureOffset(tex, tc, off.xyz).r;
    float s21 = textureOffset(tex, tc, off.zyx).r;
    float s10 = textureOffset(tex, tc, off.yxz).r;
    float s12 = textureOffset(tex, tc, off.yzx).r;

    vec3 va = normalize(vec3(size.xy,s21-s01));
    vec3 vb = normalize(vec3(size.yx,s12-s10));
    norm = normalize(norm+0.8*cross(va,vb));
    //norm = cross(va,vb);
}

void computeNormal() {
   	norm = position - cylP0 - dot(position - cylP0, cylDir)*cylDir;
	norm = (miMV*vec4(norm, 0.0)).xyz;
   	norm = normalize(norm);
}

void computeTexCoords() {
	float h = dot(position-cylP0, cylDir);
	tc = vec3(norm.x+h*0.5, h*0.3/cylR1, norm.z+h*0.5);
}

void computeDepth() {
	vec4 pp = mP * vec4(position, 1);
	float d = pp.z / pp.w;
	gl_FragDepth = d*0.5 + 0.5;
}

void applyBlinnPhong() {
	norm = gl_NormalMatrix * norm;
	vec3  light = normalize( gl_LightSource[0].position.xyz );// directional light
	float NdotL = max(dot( norm, light ), 0.0);
	vec4  ambient = gl_LightSource[0].ambient * color;
	vec4  diffuse = gl_LightSource[0].diffuse * NdotL * color;
	float NdotHV = max(dot(norm, normalize(gl_LightSource[0].halfVector.xyz)),0.0);
	vec4  specular = gl_LightSource[0].specular * pow( NdotHV, gl_FrontMaterial.shininess ); 
	gl_FragColor = ambient + diffuse + specular;
}

void main( void ) {
	miMV = inverse( gl_ModelViewMatrix ); // TODO: avoid computing inverse in shader!
	norm = Normal;
   	rayStart = vec3(0.0);
   	rayDir = MVPos;
   	rayDir = normalize(rayDir);

	position = fvObjectPosition;
	position = raycastCone();
	computeNormal();
	computeTexCoords();
	applyBumpMap();
	color = texture(tex, tc);
	applyBlinnPhong();
	computeDepth();
}

//----------------------------------------------------------------------------------------------MAIN--GP
#version 400 compatibility
#extension GL_EXT_geometry_shader4 : enable
layout (lines) in;
layout (triangle_strip, max_vertices = 12) out;
in vec2 tc[];

//Phong
out vec3 ViewDirection;
out vec3 fvObjectPosition;
out vec3 MVPos;
out vec3 Normal;
out vec3 dir;

out float cylR1;
out float cylR2;
out vec3 cylDir;
out vec3 cylP0;
out vec3 cylP1;
out vec3 cylN0;
out vec3 cylN1;

#define mMVP gl_ModelViewProjectionMatrix
#define mMV gl_ModelViewMatrix
#define mP gl_ProjectionMatrix

vec4 crns[8];
vec4 crnsMV[8];
vec4 crnsMVP[8];

void emit(int i) {
	gl_Position = crnsMVP[i];
	MVPos = crnsMV[i].xyz;
	fvObjectPosition = crnsMVP[i].xyz;
	ViewDirection = -fvObjectPosition.xyz;
	ViewDirection = normalize(ViewDirection);
	EmitVertex();
}

void emitQuad(int i1, int i2, int i3, int i4) {
	vec4 mvp1 = crnsMVP[i1];
	vec4 mvp2 = crnsMVP[i2];
	vec4 mvp3 = crnsMVP[i3];
	vec4 mvp4 = crnsMVP[i4];

	// check visibility
	vec4 c = (mvp1+mvp3)*0.5;
	vec3 i = normalize( vec3(mvp2.xyz - mvp1.xyz) );
    vec3 j = normalize( vec3(mvp4.xyz - mvp1.xyz) );
    if ( dot(cross(i, j),c.xyz) < 0.0 ) return;

	emit(i1);
	emit(i2);
	emit(i4);
	emit(i3);
	EndPrimitive();
}

vec3 perp(vec3 v) {
    vec3 b = cross(v, vec3(1, 0, 0));
    if (dot(b, b) < 0.01) b = cross(v, vec3(0, 0, 1));
    return b;
}

void main() {
	vec4 p1 = gl_PositionIn[0];
	vec4 p2 = gl_PositionIn[1];
	vec4 pm = (p1+p2)*0.5;
	dir = p2.xyz - p1.xyz;

	// make the segment bigger to avoid chamfer problems
	p1 -= vec4(dir*0.02, 0.0);
	p2 += vec4(dir*0.02, 0.0);
	dir *= 1.05;

	// cylinder and cone raycast parameters
	cylR1 = tc[0][0]*0.1;
	cylR2 = tc[1][0]*0.1;
	cylDir = normalize(vec3(mMV * vec4(dir,0.0)));
	cylP0 = vec3(mMV * p1);
	cylP1 = vec3(mMV * p2);
	cylN0 = cylDir;
	cylN1 = cylDir;

	// build hull
	vec4 Y = vec4(dir*0.5, 0.0);
	vec4 X = vec4( normalize(perp(dir)) ,0.0);
	vec4 Z = vec4( normalize(cross(dir, X.xyz)) ,0.0);
	crns[0] = pm - X*cylR1 - Y - Z*cylR1;
	crns[1] = pm + X*cylR1 - Y - Z*cylR1;
	crns[2] = pm + X*cylR1 - Y + Z*cylR1;
	crns[3] = pm - X*cylR1 - Y + Z*cylR1;
	crns[4] = pm - X*cylR1 + Y - Z*cylR1;
	crns[5] = pm + X*cylR1 + Y - Z*cylR1;
	crns[6] = pm + X*cylR1 + Y + Z*cylR1;
	crns[7] = pm - X*cylR1 + Y + Z*cylR1;
	for (int i=0; i<8; i++) crnsMV[i] = mMV * crns[i];
	for (int i=0; i<8; i++) crnsMVP[i] = mP * crnsMV[i];

	emitQuad(3, 2, 1, 0); // bottom
	emitQuad(4, 5, 6, 7); // top
	emitQuad(0, 1, 5, 4); // sides
	emitQuad(1, 2, 6, 5);
	emitQuad(2, 3, 7, 6);
	emitQuad(3, 0, 4, 7);
}

// vertex shader program
varying vec2 vtc;
varying vec3 vn;

attribute vec4 osg_Vertex;
attribute vec3 osg_Normal;
attribute vec2 osg_MultiTexCoord0;

uniform int wind; // -100 to 100

vec4 vertex;

// TODO: work on wind
void applyWind() {
	vec3 v = vertex.xyz;
	float dw = abs(float(wind)+50.0*v.x+50.0*v.z)*0.000002;
	dw *= v.y*v.y;
	dw *= sin(v.x*20.0)*sin(v.z*20.0);
	dw /= vtc[0];
    vertex += vec4(dw,0.0,dw,0.0);
}

//----------------------------------------------------------------------------------------------MAIN--VP
void main( void ) {
	vtc = osg_MultiTexCoord0;
	vn = osg_Normal;
	vertex = osg_Vertex;
	applyWind();
    gl_Position = vertex;
}

