#version 120

varying vec4 Color;
varying vec2 texCoord;

void main( void ) {
	vec4 c = vec4(1);
	c.x = gl_FragCoord.z;
	//c.y = gl_FragCoord.w;
	gl_FragColor = c;
}

#version 120

varying vec4 Color;
varying vec2 texCoord;

void main( void ) {
	vec4 c = vec4(0.0);	
	float r = sqrt(texCoord[0]*texCoord[0]+texCoord[1]*texCoord[1]);
	c.a = max(1.0-r,0.0)*0.3;
	gl_FragColor = c;
}

#version 150
#extension GL_EXT_geometry_shader4 : enable

layout (points) in;
layout (triangle_strip, max_vertices=6) out;

uniform vec2 OSGViewportSize;

in vec4 color[];
//in vec3 normal[];
out vec2 texCoord;
out vec4 Color;

void emitVertex(in vec4 p, in vec2 tc) {
	gl_Position = p;
	texCoord = tc;
	EmitVertex();
}

void emitQuad(in float s, in vec4 tc) {
	vec4 p = gl_PositionIn[0];

	float a = OSGViewportSize.y/OSGViewportSize.x;

	vec4 u = vec4(s*a,0,0,0);
	vec4 v = vec4(0,s,0,0);

	vec4 p1 = p -u -v;
	vec4 p2 = p -u +v;
	vec4 p3 = p +u +v;
	vec4 p4 = p +u -v;

	emitVertex(p1, vec2(tc[0], tc[2]));
	emitVertex(p2, vec2(tc[0], tc[3]));
	emitVertex(p3, vec2(tc[1], tc[3]));
	EndPrimitive();
	emitVertex(p1, vec2(tc[0], tc[2]));
	emitVertex(p3, vec2(tc[1], tc[3]));
	emitVertex(p4, vec2(tc[1], tc[2]));
	EndPrimitive();
}

void main() {
	Color = color[0];
	emitQuad(0.3, vec4(-1,1,-1,1));
}

#version 120

attribute vec4 osg_Vertex;
attribute vec4 osg_Color;

varying vec4 color;

void main(void) {
    gl_Position = gl_ModelViewProjectionMatrix*osg_Vertex;
    color = osg_Color;
}

import VR

